#!/bin/bash\n\nPROJECT_NAME="webinar-local"\nINVENTORY_FILE="infra/ansible/inventory_local.ini"\n\necho "[all:vars]" > $INVENTORY_FILE\necho "ansible_connection=docker" >> $INVENTORY_FILE\necho "ansible_user=root" >> $INVENTORY_FILE\necho "ansible_password=root" >> $INVENTORY_FILE\necho "ansible_python_interpreter=/usr/bin/python3" >> $INVENTORY_FILE\necho "" >> $INVENTORY_FILE\n\nfor host in database backend proxy bastion; do\n  echo "[$host]" >> $INVENTORY_FILE\n  echo "${PROJECT_NAME}-${host}" >> $INVENTORY_FILE\n  echo "" >> $INVENTORY_FILE\ndone\n\necho "Generated $INVENTORY_FILE"
